import React, { useState, useEffect } from 'react';

function App() {
  const [form, setForm] = useState({ name: '', description: '', image: null });
  const [images, setImages] = useState([]);
  const [editingId, setEditingId] = useState(null);
  const [preview, setPreview] = useState(null);
  const [lightboxImage, setLightboxImage] = useState(null);
  const [pendingDelete, setPendingDelete] = useState(null);

  const inputStyle = {
    width: '280px',
    padding: '0.5rem',
    marginBottom: '1rem',
    borderRadius: '4px',
    backgroundColor: '#2a2a2a',
    color: '#fff',
    border: '1px solid #444',
    display: 'block',
    marginLeft: 'auto',
    marginRight: 'auto'
  };

  useEffect(() => {
    fetchImages();
  }, []);

  const fetchImages = async () => {
    try {
      const res = await fetch('http://localhost:3010/images');
      const data = await res.json();
      setImages(data);
    } catch (err) {
      console.error('Erro ao buscar imagens:', err);
    }
  };

  const handleEdit = (img) => {
    setForm({ name: img.name, description: img.description, image: null });
    setEditingId(img.id);
    setPreview(`http://localhost:3010/uploads/${img.filename}`);
  };

  const handleCancel = () => {
    setForm({ name: '', description: '', image: null });
    setEditingId(null);
    setPreview(null);
  };

  const confirmDelete = (img) => {
    setPendingDelete(img);
  };

  const handleDelete = async () => {
    if (!pendingDelete) return;
    const res = await fetch(`http://localhost:3010/images/${pendingDelete.id}`, {
      method: 'DELETE'
    });
    if (res.ok) {
      if (editingId === pendingDelete.id) handleCancel();
      fetchImages();
    }
    setPendingDelete(null);
  };

  const handleImageChange = (e) => {
    const file = e.target.files[0];
    setForm((prev) => ({ ...prev, image: file }));
    if (file) {
      setPreview(URL.createObjectURL(file));
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const data = new FormData();
    data.append('name', form.name);
    data.append('description', form.description);
    if (form.image) {
      data.append('image', form.image);
    }

    const url = editingId
      ? `http://localhost:3010/images/${editingId}`
      : 'http://localhost:3010/upload';

    const method = editingId ? 'PUT' : 'POST';

    const res = await fetch(url, {
      method,
      body: data
    });

    if (res.ok) {
      handleCancel();
      fetchImages();
    } else {
      alert('Erro ao enviar imagem');
    }
  };

  const visibleImages = images.slice(-12).reverse();

  return (
    <div style={{
      backgroundColor: '#121212',
      color: '#f0f0f0',
      minHeight: '100vh',
      fontFamily: 'Arial, sans-serif',
      padding: '2rem 1rem'
    }}>
      <div style={{
        maxWidth: '400px',
        margin: '0 auto',
        backgroundColor: '#1e1e1e',
        padding: '2rem 1.5rem',
        borderRadius: '8px',
        boxShadow: '0 0 10px rgba(0,0,0,0.4)'
      }}>
        <h2 style={{ textAlign: 'center', marginBottom: '1.5rem' }}>
          {editingId ? 'Editar Imagem' : 'Upload de Imagem'}
        </h2>
        <form onSubmit={handleSubmit}>
          <input type="text" placeholder="Nome" value={form.name} onChange={(e) => setForm({ ...form, name: e.target.value })} required style={inputStyle} />
          <input type="text" placeholder="Descrição" value={form.description} onChange={(e) => setForm({ ...form, description: e.target.value })} required style={inputStyle} />
          <input type="file" accept="image/*" onChange={handleImageChange} {...(editingId ? {} : { required: true })} style={{ ...inputStyle, padding: 0 }} />

          {preview && (
            <img src={preview} alt="Prévia" style={{ width: '50%', borderRadius: '8px', marginBottom: '1rem' }} />
          )}

          <div style={{ textAlign: 'center', marginBottom: '1rem' }}>
            <button type="submit" style={{
              padding: '0.4rem 1.2rem',
              backgroundColor: '#26a69a',
              color: '#fff',
              border: 'none',
              borderRadius: '4px',
              fontSize: '0.9rem',
              cursor: 'pointer'
            }}>
              {editingId ? 'Salvar' : 'Enviar'}
            </button>
            {editingId && (
              <button type="button" onClick={handleCancel} style={{
                marginLeft: '1rem',
                padding: '0.4rem 1rem',
                backgroundColor: '#757575',
                color: '#fff',
                border: 'none',
                borderRadius: '4px',
                fontSize: '0.9rem',
                cursor: 'pointer'
              }}>
                Cancelar
              </button>
            )}
          </div>
        </form>

        <h3 style={{ textAlign: 'center', marginBottom: '1rem' }}>Imagens Salvas</h3>
        <div style={{
          display: 'grid',
          gridTemplateColumns: 'repeat(auto-fill, minmax(160px, 1fr))',
          gap: '1rem',
          justifyItems: 'center'
        }}>
          {visibleImages.map((img) => (
            <div key={img.id} style={{
              backgroundColor: '#2a2a2a',
              padding: '0.5rem',
              borderRadius: '6px',
              width: '100%'
            }}>
              <img
                src={`http://localhost:3010/uploads/${img.filename}`}
                alt={img.name}
                style={{
                  width: '100%',
                  borderRadius: '6px',
                  cursor: 'pointer',
                  marginBottom: '0.5rem'
                }}
                onClick={() => setLightboxImage(`http://localhost:3010/uploads/${img.filename}`)}
              />
              <p style={{ fontSize: '0.9rem', marginBottom: '0.5rem' }}>
                <strong>{img.name}</strong><br />{img.description}
              </p>
              <div style={{ display: 'flex', gap: '0.5rem' }}>
                <button onClick={() => handleEdit(img)} style={{
                  flex: 1,
                  backgroundColor: '#2ecc71',
                  color: '#fff',
                  border: 'none',
                  padding: '0.4rem',
                  borderRadius: '4px',
                  cursor: 'pointer',
                  fontSize: '0.8rem'
                }}>Editar</button>
                <button onClick={() => confirmDelete(img)} style={{
                  flex: 1,
                  backgroundColor: '#e74c3c',
                  color: '#fff',
                  border: 'none',
                  padding: '0.4rem',
                  borderRadius: '4px',
                  cursor: 'pointer',
                  fontSize: '0.8rem'
                }}>Remover</button>
              </div>
            </div>
          ))}
        </div>
      </div>

      {lightboxImage && (
        <div style={{
          position: 'fixed',
          top: 0, left: 0, right: 0, bottom: 0,
          backgroundColor: 'rgba(0,0,0,0.85)',
          display: 'flex',
          alignItems: 'center',
          justifyContent: 'center',
          zIndex: 9999
        }}>
          <div style={{ position: 'relative' }}>
            <img src={lightboxImage} alt="Visualização ampliada" style={{
              maxWidth: '90vw',
              maxHeight: '90vh',
              borderRadius: '8px'
            }} />
            <button onClick={() => setLightboxImage(null)} style={{
              position: 'absolute',
              top: '-10px',
              right: '-10px',
              background: '#e74c3c',
              border: 'none',
              borderRadius: '50%',
              color: '#fff',
              width: '32px',
              height: '32px',
              fontWeight: 'bold',
              cursor: 'pointer'
            }}>
              ×
            </button>
          </div>
        </div>
      )}

      {pendingDelete && (
        <div style={{
          position: 'fixed',
          top: 0, left: 0, right: 0, bottom: 0,
          backgroundColor: 'rgba(0,0,0,0.8)',
          display: 'flex',
          justifyContent: 'center',
          alignItems: 'center',
          zIndex: 9999
        }}>
          <div style={{
            background: '#1e1e1e',
            padding: '2rem',
            borderRadius: '8px',
            textAlign: 'center',
            maxWidth: '90%',
            color: '#fff'
          }}>
            <p>Deseja realmente remover <strong>{pendingDelete.name}</strong>?</p>
            <div style={{ display: 'flex', justifyContent: 'center', gap: '1rem', marginTop: '1rem' }}>
              <button onClick={handleDelete} style={{
                padding: '0.5rem 1.5rem',
                backgroundColor: '#e74c3c',
                color: '#fff',
                border: 'none',
                borderRadius: '4px',
                cursor: 'pointer'
              }}>Remover</button>
              <button onClick={() => setPendingDelete(null)} style={{
                padding: '0.5rem 1.5rem',
                backgroundColor: '#555',
                color: '#fff',
                border: 'none',
                borderRadius: '4px',
                cursor: 'pointer'
              }}>Cancelar</button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

export default App;
