import React, { useState, useEffect } from 'react';

function App() {
  const [form, setForm] = useState({ name: '', description: '', image: null });
  const [images, setImages] = useState([]);
  const [editingId, setEditingId] = useState(null);
  const [preview, setPreview] = useState(null);

  useEffect(() => {
    fetchImages();
  }, []);

  const fetchImages = async () => {
    const res = await fetch('http://localhost:3010/images');
    const data = await res.json();
    setImages(data);
  };

  const handleEdit = (img) => {
    setForm({ name: img.name, description: img.description, image: null });
    setEditingId(img.id);
    setPreview(`http://localhost:3010/uploads/${img.filename}`);
  };

  const handleCancel = () => {
    setForm({ name: '', description: '', image: null });
    setEditingId(null);
    setPreview(null);
  };

  const handleImageChange = (e) => {
    const file = e.target.files[0];
    setForm((prev) => ({ ...prev, image: file }));
    if (file) {
      setPreview(URL.createObjectURL(file));
    }
  };

  const handleDelete = async (id) => {
    const confirm = window.confirm('Tem certeza que deseja apagar esta imagem?');
    if (!confirm) return;
  
    const res = await fetch(`http://localhost:3010/images/${id}`, {
      method: 'DELETE'
    });
  
    if (res.ok) {
      fetchImages();
      if (id === editingId) {
        setForm({ name: '', description: '', image: null });
        setEditingId(null);
        setPreview(null);
      }
    } else {
      alert('Erro ao apagar imagem.');
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    const data = new FormData();
    data.append('name', form.name);
    data.append('description', form.description);
    if (form.image) {
      data.append('image', form.image);
    }

    const method = editingId ? 'PUT' : 'POST';
    const url = editingId
      ? `http://localhost:3010/images/${editingId}`
      : 'http://localhost:3010/upload';

    const res = await fetch(url, {
      method,
      body: data
    });

    if (!res.ok) {
      console.error('Erro ao enviar');
      return;
    }

    setForm({ name: '', description: '', image: null });
    setEditingId(null);
    setPreview(null);
    fetchImages();
  };

  return (
    <div style={{ padding: '1rem', maxWidth: 600, margin: '0 auto' }}>
      <h2>{editingId ? 'Editar Imagem' : 'Upload de Imagem'}</h2>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          placeholder="Nome"
          value={form.name}
          onChange={(e) => setForm({ ...form, name: e.target.value })}
          required
        /><br />
        <input
          type="text"
          placeholder="Descrição"
          value={form.description}
          onChange={(e) => setForm({ ...form, description: e.target.value })}
          required
        /><br />
        <input
          type="file"
          accept="image/*"
          onChange={handleImageChange}
          {...(editingId ? {} : { required: true })}
        /><br />
        {preview && (
          <img
            src={preview}
            alt="Prévia"
            width="100"
            style={{ display: 'block', marginTop: '0.5rem' }}
          />
        )}
        <button type="submit" style={{ marginTop: '0.5rem' }}>
          {editingId ? 'Salvar Alterações' : 'Enviar'}
        </button>
        {editingId && (
          <button type="button" onClick={handleCancel} style={{ marginLeft: '1rem' }}>
            Cancelar
          </button>
        )}
      </form>

      <h3 style={{ marginTop: '2rem' }}>Imagens Salvas</h3>
      <ul style={{ listStyle: 'none', padding: 0 }}>
        {images.map((img) => (
          <li key={img.id} style={{ marginBottom: '1rem', borderBottom: '1px solid #ccc', paddingBottom: '1rem' }}>
            <img
              src={`http://localhost:3010/uploads/${img.filename}`}
              alt={img.name}
              width="100"
              style={{ display: 'block', marginBottom: '0.5rem' }}
            />
            <p>
              <strong>{img.name}</strong><br />
              {img.description}
            </p>
            <button onClick={() => handleEdit(img)}>Editar</button>
            <button onClick={() => handleEdit(img)}>Editar</button>
<button onClick={() => handleDelete(img.id)} style={{ marginLeft: '0.5rem' }}>
  Remover
</button>

          </li>
        ))}
      </ul>
    </div>
  );
}

export default App;
