import React, { useState, useEffect } from "react";
import { FiEdit2, FiTrash2 } from "react-icons/fi";

function App() {
  const [form, setForm] = useState({ name: "", description: "", image: null });
  const [images, setImages] = useState([]);
  const [editingId, setEditingId] = useState(null);
  const [preview, setPreview] = useState(null);
  const [lightboxImage, setLightboxImage] = useState(null);
  const [pendingDelete, setPendingDelete] = useState(null);
  const [searchInput, setSearchInput] = useState("");
  const [searchTerm, setSearchTerm] = useState("");
  const [page, setPage] = useState(1);
  const [loading, setLoading] = useState(false);
  const [hasMore, setHasMore] = useState(true);
  const [total, setTotal] = useState(0);
  const limit = 10;
  const [sortOrder, setSortOrder] = useState("recent");

  const inputStyle = {
    width: "480px",
    padding: "0.5rem",
    marginBottom: "1rem",
    borderRadius: "4px",
    backgroundColor: "#2a2a2a",
    color: "#fff",
    border: "1px solid #444",
    display: "block",
    marginLeft: "auto",
    marginRight: "auto",
  };

  const fetchImages = async (
    pageToLoad = page,
    term = searchTerm,
    sort = sortOrder
  ) => {
    try {
      setLoading(true);
      const res = await fetch(
        `http://localhost:3010/images?q=${encodeURIComponent(
          term
        )}&page=${pageToLoad}&limit=${limit}&sort=${sort}`
      );

      const result = await res.json();
      setImages(result.data);
      setTotal(result.total);
      setHasMore(result.data.length === 4);
    } catch (err) {
      console.error("Erro ao buscar imagens:", err);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchImages(page, searchTerm, sortOrder);
  }, [page, searchTerm, sortOrder]);

  const handleEdit = (img) => {
    setForm({ name: img.name, description: img.description, image: null });
    setEditingId(img.id);
    setPreview(`http://localhost:3010/uploads/${img.filename}`);
  };

  const handleCancel = () => {
    setForm({ name: "", description: "", image: null });
    setEditingId(null);
    setPreview(null);
  };

  const confirmDelete = (img) => {
    setPendingDelete(img);
  };

  const handleDelete = async () => {
    if (!pendingDelete) return;
    const res = await fetch(
      `http://localhost:3010/images/${pendingDelete.id}`,
      {
        method: "DELETE",
      }
    );
    if (res.ok) {
      if (editingId === pendingDelete.id) handleCancel();
      fetchImages();
    }
    setPendingDelete(null);
  };

  const handleImageChange = (e) => {
    const file = e.target.files[0];
    setForm((prev) => ({ ...prev, image: file }));
    if (file) {
      setPreview(URL.createObjectURL(file));
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    const data = new FormData();
    data.append("name", form.name);
    data.append("description", form.description);
    if (form.image) {
      data.append("image", form.image);
    }

    const url = editingId
      ? `http://localhost:3010/images/${editingId}`
      : "http://localhost:3010/upload";

    const method = editingId ? "PUT" : "POST";

    try {
      const res = await fetch(url, {
        method,
        body: data,
      });

      if (!res.ok) {
        alert("Erro ao enviar imagem");
        return;
      }

      // âœ… Limpa formulÃ¡rio e reseta estados
      setForm({ name: "", description: "", image: null });
      setEditingId(null);
      setPreview(null);
      setPage(1);
      setSearchTerm(searchInput);
      fetchImages(1, searchInput); // forÃ§a atualizar imediatamente
    } catch (err) {
      console.error("Erro no envio:", err);
      alert("Erro ao enviar imagem");
    }
  };

  const visibleImages = Array.isArray(images) ? images : [];

  return (
    <div
      style={{
        backgroundColor: "#121212",
        color: "#f0f0f0",
        minHeight: "100vh",
        fontFamily: "Arial, sans-serif",
        padding: "2rem",
      }}
    >
      <div
        style={{
          backgroundColor: "#1e1e1e",
          padding: "2rem",
          borderRadius: "8px",
          flex: "1",
          boxShadow: "0 0 10px rgba(0,0,0,0.4)",
        }}
      >
        <h2 style={{ textAlign: "center", marginBottom: "1.5rem" }}>
          {editingId ? "Editar Imagem" : "Upload de Imagem"}
        </h2>
        <form onSubmit={handleSubmit}>
          <input
            type="text"
            placeholder="Nome"
            value={form.name}
            onChange={(e) => setForm({ ...form, name: e.target.value })}
            required
            style={inputStyle}
          />
          <input
            type="text"
            placeholder="DescriÃ§Ã£o"
            value={form.description}
            onChange={(e) => setForm({ ...form, description: e.target.value })}
            required
            style={inputStyle}
          />
          <label
            style={{
              display: "block",
              width: "280px",
              margin: "0 auto 1rem",
              backgroundColor: "#2a2a2a",
              color: "#ccc",
              padding: "0.5rem",
              textAlign: "center",
              borderRadius: "4px",
              border: "1px solid #444",
              cursor: "pointer",
              fontSize: "0.9rem",
            }}
          >
            Selecionar imagem
            <input
              type="file"
              accept="image/*"
              onChange={handleImageChange}
              {...(editingId ? {} : { required: true })}
              style={{
                display: "none",
              }}
            />
          </label>

          {preview && (
            <div style={{ textAlign: "center", marginBottom: "1rem" }}>
              <img
                src={preview}
                alt="PrÃ©via"
                style={{
                  maxWidth: "200px",
                  maxHeight: "200px",
                  width: "100%",
                  height: "auto",
                  borderRadius: "6px",
                  boxShadow: "0 0 8px rgba(0,0,0,0.5)",
                  objectFit: "contain",
                }}
              />
            </div>
          )}

          <div style={{ textAlign: "center", marginBottom: "1rem" }}>
            <button
              type="submit"
              style={{
                padding: "0.4rem 1.2rem",
                backgroundColor: "#26a69a",
                color: "#fff",
                border: "none",
                borderRadius: "4px",
                fontSize: "0.9rem",
                cursor: "pointer",
              }}
            >
              {editingId ? "Salvar" : "Enviar"}
            </button>
            {editingId && (
              <button
                type="button"
                onClick={handleCancel}
                style={{
                  marginLeft: "1rem",
                  padding: "0.4rem 1rem",
                  backgroundColor: "#757575",
                  color: "#fff",
                  border: "none",
                  borderRadius: "4px",
                  fontSize: "0.9rem",
                  cursor: "pointer",
                }}
              >
                Cancelar
              </button>
            )}
          </div>
        </form>

        <h3 style={{ textAlign: "center", marginBottom: "1rem" }}>
          Imagens Salvas
        </h3>
        {!loading && searchTerm && (
          <p
            style={{ textAlign: "center", color: "#ccc", marginBottom: "1rem" }}
          >
            {total === 0
              ? "Nenhuma imagem encontrada"
              : `${total} imagem${total > 1 ? "s" : ""} encontrada${
                  total > 1 ? "s" : ""
                }`}
          </p>
        )}

        <form
          onSubmit={(e) => {
            e.preventDefault();
            setPage(1);
            setSearchTerm(searchInput);
          }}
          style={{
            textAlign: "center",
            marginBottom: "1rem",
            display: "flex",
            justifyContent: "center",
            gap: "0.5rem",
            flexWrap: "wrap",
          }}
        >
          <input
            type="text"
            placeholder="Buscar por nome ou descriÃ§Ã£o..."
            value={searchInput}
            onChange={(e) => setSearchInput(e.target.value)}
            style={{
              maxWidth: "280px",
              padding: "0.5rem",
              borderRadius: "4px",
              backgroundColor: "#2a2a2a",
              color: "#fff",
              border: "1px solid #444",
              flex: "1 0 auto",
            }}
          />
          <button
            type="submit"
            style={{
              padding: "0.5rem 1rem",
              backgroundColor: "#26a69a",
              color: "#fff",
              border: "none",
              borderRadius: "4px",
              cursor: "pointer",
            }}
          >
            Buscar
          </button>

          <button
            type="button"
            onClick={() => {
              setSearchInput("");
              setSearchTerm("");
            }}
            style={{
              padding: "0.5rem 1rem",
              backgroundColor: "#757575",
              color: "#fff",
              border: "none",
              borderRadius: "4px",
              cursor: "pointer",
            }}
          >
            Limpar
          </button>
        </form>
        <select
          value={sortOrder}
          onChange={(e) => {
            setSortOrder(e.target.value);
            setPage(1); // isso jÃ¡ dispara useEffect com sortOrder
          }}
          style={inputStyle}
        >
          <option value="recent">Mais recente</option>
          <option value="oldest">Mais antigo</option>
          <option value="az">Aâ€“Z</option>
          <option value="za">Zâ€“A</option>
        </select>

        <div
          style={{
            display: "grid",
            gridTemplateColumns: "repeat(auto-fill, minmax(160px, 1fr))",
            gap: "1.5rem",
            justifyContent: "center", // ðŸ‘ˆ centraliza o grid
            maxWidth: "1000px", // ðŸ‘ˆ largura mÃ¡xima opcional
            margin: "0 auto", // ðŸ‘ˆ centraliza o bloco na tela
          }}
        >
          {visibleImages.map((img) => (
            <div
              key={img.id}
              style={{
                backgroundColor: "#2a2a2a",
                padding: "0.5rem",
                borderRadius: "6px",
                width: "100%",
              }}
            >
              <div
                style={{
                  height: "200px",
                  backgroundColor: "#1a1a1a",
                  borderRadius: "6px",
                  marginBottom: "0.5rem",
                  display: "flex",
                  alignItems: "center",
                  justifyContent: "center",
                  overflow: "hidden",
                }}
              >
                <img
                  src={`http://localhost:3010/uploads/${img.filename}`}
                  alt={img.name}
                  style={{
                    maxWidth: "100%",
                    maxHeight: "100%",
                    objectFit: "contain",
                    cursor: "pointer",
                  }}
                  onClick={() =>
                    setLightboxImage(
                      `http://localhost:3010/uploads/${img.filename}`
                    )
                  }
                />
              </div>

              <p style={{ fontSize: "0.9rem", marginBottom: "0.5rem" }}>
                <strong>{img.name}</strong>
                <br />
                {img.description}
                <br />
                <span style={{ fontSize: "0.8rem", color: "#aaa" }}>
                  Data: {new Date(img.created_at).toLocaleDateString("pt-BR")}
                </span>
                <p style={{ fontSize: "0.8rem", color: "#aaa" }}>
                  Hora:{" "}
                  {new Date(img.created_at).toLocaleTimeString("pt-BR", {
                    hour: "2-digit",
                    minute: "2-digit",
                    hour12: false,
                  })}
                </p>
              </p>

              <div style={{ display: "flex", gap: "0.5rem" }}>
                <button
                  onClick={() => handleEdit(img)}
                  style={{
                    flex: 1,
                    backgroundColor: "#2ecc71",
                    color: "#fff",
                    border: "none",
                    padding: "0.4rem",
                    borderRadius: "4px",
                    cursor: "pointer",
                    fontSize: "0.9rem",
                    display: "flex",
                    alignItems: "center",
                    justifyContent: "center",
                    gap: "0.4rem",
                  }}
                >
                  <FiEdit2 /> Editar
                </button>
                <button
                  onClick={() => confirmDelete(img)}
                  style={{
                    flex: 1,
                    backgroundColor: "#e74c3c",
                    color: "#fff",
                    border: "none",
                    padding: "0.4rem",
                    borderRadius: "4px",
                    cursor: "pointer",
                    fontSize: "0.9rem",
                    display: "flex",
                    alignItems: "center",
                    justifyContent: "center",
                    gap: "0.4rem",
                  }}
                >
                  <FiTrash2 /> Remover
                </button>
              </div>
            </div>
          ))}

          {/* ðŸ”§ Preenche atÃ© alcanÃ§ar o nÃºmero fixo de itens por pÃ¡gina */}
          {Array.from({ length: limit - visibleImages.length }).map(
            (_, index) => (
              <div
                key={`placeholder-${index}`}
                style={{
                  backgroundColor: "#2a2a2a",
                  padding: "0.5rem",
                  borderRadius: "6px",
                  width: "100%",
                  height: "260px", // mesma altura do bloco real (imagem + texto + botÃµes)
                  opacity: 0, // ðŸ‘ˆ ocupa espaÃ§o, mas invisÃ­vel
                  pointerEvents: "none",
                }}
              />
            )
          )}
          <div
            style={{
              height: "1.5rem", // mesmo valor do gap do grid
              width: "100%",
              opacity: 0,
              pointerEvents: "none",
            }}
          />
        </div>
        <div style={{ textAlign: "center", marginTop: "1.5rem" }}>
          {[...Array(Math.ceil(total / limit))].map((_, i) => (
            <button
              key={i}
              onClick={() => setPage(i + 1)}
              style={{
                margin: "0 0.25rem",
                padding: "0.5rem 0.9rem",
                backgroundColor: page === i + 1 ? "#26a69a" : "#333",
                color: "#fff",
                border: "none",
                borderRadius: "4px",
                cursor: "pointer",
              }}
            >
              {i + 1}
            </button>
          ))}
        </div>
        <div
          style={{
            textAlign: "center",
            marginTop: "1.5rem",
          }}
        >
          <button
            onClick={() => setPage((p) => Math.max(1, p - 1))}
            disabled={page === 1}
            style={{
              padding: "0.5rem 1rem",
              backgroundColor: page === 1 ? "#444" : "#26a69a",
              color: "#fff",
              border: "none",
              borderRadius: "4px",
              cursor: page === 1 ? "not-allowed" : "pointer",
              marginRight: "1rem",
              opacity: page === 1 ? 0.6 : 1,
            }}
          >
            PÃ¡gina anterior
          </button>
          <button
            onClick={() => setPage((p) => p + 1)}
            disabled={!hasMore}
            style={{
              padding: "0.5rem 1rem",
              backgroundColor: !hasMore ? "#444" : "#26a69a",
              color: "#fff",
              border: "none",
              borderRadius: "4px",
              cursor: !hasMore ? "not-allowed" : "pointer",
              opacity: !hasMore ? 0.6 : 1,
            }}
          >
            PrÃ³xima pÃ¡gina
          </button>
        </div>
      </div>

      {lightboxImage && (
        <div
          style={{
            position: "fixed",
            top: 0,
            left: 0,
            right: 0,
            bottom: 0,
            backgroundColor: "rgba(0,0,0,0.85)",
            display: "flex",
            alignItems: "center",
            justifyContent: "center",
            zIndex: 9999,
          }}
        >
          <div style={{ position: "relative" }}>
            <img
              src={lightboxImage}
              alt="VisualizaÃ§Ã£o ampliada"
              style={{
                maxWidth: "90vw",
                maxHeight: "90vh",
                borderRadius: "8px",
              }}
            />
            <button
              onClick={() => setLightboxImage(null)}
              style={{
                position: "absolute",
                top: "-10px",
                right: "-10px",
                background: "#e74c3c",
                border: "none",
                borderRadius: "50%",
                color: "#fff",
                width: "32px",
                height: "32px",
                fontWeight: "bold",
                cursor: "pointer",
              }}
            >
              Ã—
            </button>
          </div>
        </div>
      )}

      {pendingDelete && (
        <div
          style={{
            position: "fixed",
            top: 0,
            left: 0,
            right: 0,
            bottom: 0,
            backgroundColor: "rgba(0,0,0,0.8)",
            display: "flex",
            justifyContent: "center",
            alignItems: "center",
            zIndex: 9999,
          }}
        >
          <div
            style={{
              background: "#1e1e1e",
              padding: "2rem",
              borderRadius: "8px",
              textAlign: "center",
              maxWidth: "90%",
              color: "#fff",
            }}
          >
            <p>
              Deseja realmente remover <strong>{pendingDelete.name}</strong>?
            </p>
            <div
              style={{
                display: "flex",
                justifyContent: "center",
                gap: "1rem",
                marginTop: "1rem",
              }}
            >
              <button
                onClick={handleDelete}
                style={{
                  padding: "0.5rem 1.5rem",
                  backgroundColor: "#e74c3c",
                  color: "#fff",
                  border: "none",
                  borderRadius: "4px",
                  cursor: "pointer",
                }}
              >
                Remover
              </button>
              <button
                onClick={() => setPendingDelete(null)}
                style={{
                  padding: "0.5rem 1.5rem",
                  backgroundColor: "#555",
                  color: "#fff",
                  border: "none",
                  borderRadius: "4px",
                  cursor: "pointer",
                }}
              >
                Cancelar
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

export default App;
