import React, { useState } from "react";

function Login({ onLogin, onShowRegister }) {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");

  const handleSubmit = async (e) => {
    e.preventDefault();

    const res = await fetch("http://localhost:3010/login", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ username, password }),
    });

    if (res.ok) {
      const data = await res.text(); // ou .json() se retornar token
      onLogin(username); // ou salvar token
    } else {
      setError("Usuário ou senha inválidos");
    }
  };

  return (
    <div style={{ maxWidth: "400px", margin: "4rem auto", color: "#fff" }}>
      <div style={{ textAlign: "right", marginBottom: "1rem" }}>
        <button
          onClick={() => setUser(null)}
          style={{
            backgroundColor: "#e74c3c",
            color: "#fff",
            border: "none",
            padding: "0.4rem 1rem",
            borderRadius: "4px",
            cursor: "pointer",
          }}
        >
          Sair
        </button>
      </div>

      <h2 style={{ textAlign: "center" }}>Login</h2>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          placeholder="Usuário"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
          required
          style={{ width: "100%", padding: "0.5rem", marginBottom: "1rem" }}
        />
        <input
          type="password"
          placeholder="Senha"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          required
          style={{ width: "100%", padding: "0.5rem", marginBottom: "1rem" }}
        />
        <button
          type="submit"
          style={{
            width: "100%",
            padding: "0.5rem",
            backgroundColor: "#26a69a",
            color: "#fff",
            border: "none",
            borderRadius: "4px",
          }}
        >
          Entrar
        </button>
        <button onClick={onShowRegister}>Não tem conta? Cadastre-se</button>

        {error && (
          <p style={{ color: "#e74c3c", marginTop: "1rem" }}>{error}</p>
        )}
      </form>
    </div>
  );
}

export default Login;
