import React, { useState, useEffect } from 'react';
import axios from 'axios';

function App() {
  const [form, setForm] = useState({ name: '', description: '', image: null });
  const [images, setImages] = useState([]);
  const [editingId, setEditingId] = useState(null);

  const fetchImages = async () => {
    const res = await axios.get('http://localhost:3001/images');
    setImages(res.data);
  };

  useEffect(() => { fetchImages(); }, []);

  const handleEdit = (img) => {
    setForm({ name: img.name, description: img.description, image: null });
    setEditingId(img.id);
  };

  const handleCancel = () => {
    setForm({ name: '', description: '', image: null });
    setEditingId(null);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
  
    const data = new FormData();
    data.append('name', form.name);
    data.append('description', form.description);
    if (form.image) {
      data.append('image', form.image);
    }
  
    if (editingId) {
      await axios.put(`http://localhost:3001/images/${editingId}`, data);
      setEditingId(null);
    } else {
      await axios.post('http://localhost:3001/upload', data);
    }
  

    setForm({ name: '', description: '', image: null });
    fetchImages();
  };

  return (
    <div style={{ padding: '1rem', maxWidth: 600 }}>
      <h2>{editingId ? 'Editar Imagem' : 'Upload de Imagem'}</h2>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          placeholder="Nome"
          value={form.name}
          onChange={(e) => setForm({ ...form, name: e.target.value })}
          required
        /><br />
        <input
          type="text"
          placeholder="Descrição"
          value={form.description}
          onChange={(e) => setForm({ ...form, description: e.target.value })}
          required
        /><br />
        {!editingId && (
          <input
            type="file"
            accept="image/*"
            onChange={(e) => setForm({ ...form, image: e.target.files[0] })}
            required
          />
        )}
        <br />
        <button type="submit">{editingId ? 'Salvar Alterações' : 'Enviar'}</button>
        {editingId && <button onClick={handleCancel} type="button">Cancelar</button>}
      </form>

      <h3>Imagens Salvas</h3>
      <ul style={{ listStyle: 'none', padding: 0 }}>
        {images.map((img) => (
          <li key={img.id} style={{ marginBottom: '1rem' }}>
            <img
              src={`http://localhost:3001/uploads/${img.filename}`}
              alt={img.name}
              width="100"
              style={{ display: 'block' }}
            />
            <p>
              <strong>{img.name}</strong><br />
              {img.description}
            </p>
            <button onClick={() => handleEdit(img)}>Editar</button>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default App;
