const express = require("express");
const multer = require("multer");
const cors = require("cors");
const path = require("path");
const fs = require("fs");
const bcrypt = require("bcrypt");
const db = require("./db");

const app = express();
const PORT = 3010;

app.use(cors());
app.use(express.json());
app.use("/uploads", express.static(path.join(__dirname, "uploads")));

// Multer config
const storage = multer.diskStorage({
  destination: "uploads/",
  filename: (req, file, cb) => {
    const uniqueName = Date.now() + path.extname(file.originalname);
    cb(null, uniqueName);
  },
});
const upload = multer({ storage });

// Upload de imagem
app.post("/upload", upload.single("image"), (req, res) => {
  const { name, description } = req.body;
  const filename = req.file.filename;
  const createdAt = new Date().toISOString();

  db.run(
    "INSERT INTO images (name, description, filename, created_at) VALUES (?, ?, ?, ?)",
    [name, description, filename, createdAt],
    function (err) {
      if (err) return res.status(500).send(err.message);
      res.json({
        id: this.lastID,
        name,
        description,
        filename,
        created_at: createdAt,
      });
    }
  );
});

// Atualizar imagem
app.put("/images/:id", upload.single("image"), (req, res) => {
  const { id } = req.params;
  const { name, description } = req.body;
  const newFile = req.file?.filename;

  db.get("SELECT filename FROM images WHERE id = ?", [id], (err, row) => {
    if (err) return res.status(500).send(err.message);
    if (!row) return res.status(404).send("Imagem n칚o encontrada");

    const oldFilePath = path.join(__dirname, "uploads", row.filename);

    const query = newFile
      ? "UPDATE images SET name = ?, description = ?, filename = ? WHERE id = ?"
      : "UPDATE images SET name = ?, description = ? WHERE id = ?";
    const params = newFile
      ? [name, description, newFile, id]
      : [name, description, id];

    db.run(query, params, function (err) {
      if (err) return res.status(500).send(err.message);
      if (newFile && fs.existsSync(oldFilePath)) {
        fs.unlink(oldFilePath, () => {});
      }
      res.send({ id, name, description, filename: newFile || row.filename });
    });
  });
});

// Registrar usu치rio
app.post("/register", async (req, res) => {
  const { username, password } = req.body;
  if (!username || !password)
    return res.status(400).send("Campos obrigat칩rios");

  try {
    const password_hash = await bcrypt.hash(password, 10);
    db.run(
      "INSERT INTO users (username, password_hash) VALUES (?, ?)",
      [username, password_hash],
      function (err) {
        if (err) return res.status(500).send("Erro ao registrar");
        res.status(201).send("Usu치rio registrado com sucesso");
      }
    );
  } catch (err) {
    res.status(500).send("Erro no servidor");
  }
});

// Login
app.post("/login", (req, res) => {
  const { username, password } = req.body;

  db.get(
    "SELECT * FROM users WHERE username = ?",
    [username],
    async (err, user) => {
      if (err || !user) return res.status(401).send("Usu치rio n칚o encontrado");

      const match = await bcrypt.compare(password, user.password_hash);
      if (!match) return res.status(401).send("Senha incorreta");

      res.send("Login bem-sucedido");
    }
  );
});

// Listar imagens
app.get("/images", (req, res) => {
  const search = req.query.q || "";
  const page = parseInt(req.query.page) || 1;
  const limit = parseInt(req.query.limit) || 10;
  const offset = (page - 1) * limit;
  const like = `%${search}%`;
  const sort = req.query.sort || "recent";

  let orderBy = "id DESC";
  if (sort === "oldest") orderBy = "id ASC";
  if (sort === "az") orderBy = "name COLLATE NOCASE ASC";
  if (sort === "za") orderBy = "name COLLATE NOCASE DESC";

  const queryData = `
    SELECT * FROM images
    WHERE name LIKE ? OR description LIKE ?
    ORDER BY ${orderBy}
    LIMIT ? OFFSET ?
  `;
  const queryCount = `
    SELECT COUNT(*) as total FROM images
    WHERE name LIKE ? OR description LIKE ?
  `;

  db.all(queryData, [like, like, limit, offset], (err, rows) => {
    if (err) return res.status(500).send(err.message);
    db.get(queryCount, [like, like], (err2, result) => {
      if (err2) return res.status(500).send(err2.message);
      res.json({ data: rows, total: result.total });
    });
  });
});

// Deletar imagem
app.delete("/images/:id", (req, res) => {
  const { id } = req.params;

  db.get("SELECT filename FROM images WHERE id = ?", [id], (err, row) => {
    if (err) return res.status(500).send(err.message);
    if (!row) return res.status(404).send("Imagem n칚o encontrada");

    const filePath = path.join(__dirname, "uploads", row.filename);

    db.run("DELETE FROM images WHERE id = ?", [id], (err) => {
      if (err) return res.status(500).send(err.message);
      if (fs.existsSync(filePath)) {
        fs.unlink(filePath, () => {});
      }
      res.sendStatus(204);
    });
  });
});

app.listen(PORT, () => {
  console.log(`游 Servidor rodando em http://localhost:${PORT}`);
});
