import React, { useState, useEffect } from 'react';
import axios from 'axios';

function App() {
  const [form, setForm] = useState({ name: '', description: '', image: null });
  const [images, setImages] = useState([]);

  const fetchImages = async () => {
    const res = await axios.get('http://localhost:3001/images');
    setImages(res.data);
  };

  useEffect(() => { fetchImages(); }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();
    const data = new FormData();
    data.append('name', form.name);
    data.append('description', form.description);
    data.append('image', form.image);

    await axios.post('http://localhost:3001/upload', data);
    setForm({ name: '', description: '', image: null });
    fetchImages();
  };

  return (
    <div style={{ padding: '1rem' }}>
      <h2>Upload de Imagem</h2>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          placeholder="Nome"
          value={form.name}
          onChange={(e) => setForm({ ...form, name: e.target.value })}
        /><br />
        <input
          type="text"
          placeholder="Descrição"
          value={form.description}
          onChange={(e) => setForm({ ...form, description: e.target.value })}
        /><br />
        <input
          type="file"
          accept="image/*"
          onChange={(e) => setForm({ ...form, image: e.target.files[0] })}
        /><br />
        <button type="submit">Enviar</button>
      </form>

      <h3>Imagens Salvas</h3>
      <ul>
        {images.map((img) => (
          <li key={img.id}>
            <img
              src={`http://localhost:3001/uploads/${img.filename}`}
              alt={img.name}
              width="100"
            />
            <p><strong>{img.name}</strong>: {img.description}</p>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default App;
