import React, { useState } from "react";

function Register({ onRegisterSuccess }) {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [confirm, setConfirm] = useState("");
  const [error, setError] = useState("");
  const [message, setMessage] = useState("");

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (password !== confirm) {
      setError("As senhas não coincidem");
      return;
    }

    const res = await fetch("http://localhost:3010/register", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ username, password }),
    });

    if (res.ok) {
      setMessage("Cadastro realizado com sucesso!");
      setError("");
      setUsername("");
      setPassword("");
      setConfirm("");
      onRegisterSuccess && onRegisterSuccess(); // ex: redirecionar pro login
    } else {
      setError("Erro ao cadastrar. Verifique se o nome de usuário já existe.");
      setMessage("");
    }
  };

  return (
    <div style={{ maxWidth: "400px", margin: "4rem auto", color: "#fff" }}>
      <h2 style={{ textAlign: "center" }}>Criar nova conta</h2>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          placeholder="Usuário"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
          required
          style={{ width: "100%", padding: "0.5rem", marginBottom: "1rem" }}
        />
        <input
          type="password"
          placeholder="Senha"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          required
          style={{ width: "100%", padding: "0.5rem", marginBottom: "1rem" }}
        />
        <input
          type="password"
          placeholder="Confirmar senha"
          value={confirm}
          onChange={(e) => setConfirm(e.target.value)}
          required
          style={{ width: "100%", padding: "0.5rem", marginBottom: "1rem" }}
        />
        <button
          type="submit"
          style={{
            width: "100%",
            padding: "0.5rem",
            backgroundColor: "#26a69a",
            color: "#fff",
            border: "none",
            borderRadius: "4px",
          }}
        >
          Cadastrar
        </button>
        {error && (
          <p style={{ color: "#e74c3c", marginTop: "1rem" }}>{error}</p>
        )}
        {message && (
          <p style={{ color: "#2ecc71", marginTop: "1rem" }}>{message}</p>
        )}
      </form>
    </div>
  );
}

export default Register;
