import React, { useState, useEffect } from 'react';

function App() {
  const [form, setForm] = useState({ name: '', description: '', image: null });
  const [images, setImages] = useState([]);
  const [editingId, setEditingId] = useState(null);
  const [preview, setPreview] = useState(null);
  const [lightboxImage, setLightboxImage] = useState(null);
  const [pendingDelete, setPendingDelete] = useState(null);


  useEffect(() => {
    fetchImages();
  }, []);

  const fetchImages = async () => {
    try {
      const res = await fetch('http://localhost:3010/images');
      const data = await res.json();
      setImages(data);
    } catch (err) {
      console.error('Erro ao buscar imagens:', err);
    }
  };

  const handleEdit = (img) => {
    setForm({ name: img.name, description: img.description, image: null });
    setEditingId(img.id);
    setPreview(`http://localhost:3010/uploads/${img.filename}`);
  };

  const handleCancel = () => {
    setForm({ name: '', description: '', image: null });
    setEditingId(null);
    setPreview(null);
  };

  const confirmDelete = (img) => {
    setPendingDelete(img);
  };
  
  const handleDelete = async () => {
    const id = pendingDelete?.id;
    if (!id) return;
    const res = await fetch(`http://localhost:3010/images/${id}`, {
      method: 'DELETE'
    });
    if (res.ok) {
      if (id === editingId) handleCancel();
      fetchImages();
    } else {
      alert('Erro ao remover imagem.');
    }
    setPendingDelete(null);
  };
  

  const handleImageChange = (e) => {
    const file = e.target.files[0];
    setForm((prev) => ({ ...prev, image: file }));
    if (file) {
      setPreview(URL.createObjectURL(file));
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const data = new FormData();
    data.append('name', form.name);
    data.append('description', form.description);
    if (form.image) {
      data.append('image', form.image);
    }

    const url = editingId
      ? `http://localhost:3010/images/${editingId}`
      : 'http://localhost:3010/upload';

    const method = editingId ? 'PUT' : 'POST';

    const res = await fetch(url, {
      method,
      body: data
    });

    if (!res.ok) {
      alert('Erro ao enviar imagem');
      return;
    }

    handleCancel();
    fetchImages();
  };

  const visibleImages = images.slice(-12).reverse();

  return (
    <div style={{
      backgroundColor: '#121212',
      color: '#f0f0f0',
      minHeight: '100vh',
      padding: '2rem',
      fontFamily: 'Arial, sans-serif'
    }}>
      <div style={{
        maxWidth: 480,
        margin: '0 auto',
        padding: '1rem',
        backgroundColor: '#1e1e1e',
        borderRadius: '8px',
        boxShadow: '0 0 10px rgba(0,0,0,0.4)'
      }}>
        <h2 style={{ textAlign: 'center', marginBottom: '1rem' }}>
          {editingId ? 'Editar Imagem' : 'Upload de Imagem'}
        </h2>
        <form onSubmit={handleSubmit}>
          <input
            type="text"
            placeholder="Nome"
            value={form.name}
            onChange={(e) => setForm({ ...form, name: e.target.value })}
            required
            style={{ width: '100%', marginBottom: '0.5rem', padding: '0.5rem' }}
          />
          <input
            type="text"
            placeholder="Descrição"
            value={form.description}
            onChange={(e) => setForm({ ...form, description: e.target.value })}
            required
            style={{ width: '100%', marginBottom: '0.5rem', padding: '0.5rem' }}
          />
          <input
            type="file"
            accept="image/*"
            onChange={handleImageChange}
            {...(editingId ? {} : { required: true })}
            style={{ marginBottom: '0.5rem' }}
          />
          {preview && (
            <img
              src={preview}
              alt="Prévia"
              width="100%"
              style={{ marginBottom: '0.5rem', borderRadius: '8px' }}
            />
          )}
          <div style={{ display: 'flex', justifyContent: 'space-between' }}>
            <button type="submit" style={{
              padding: '0.5rem 1rem',
              backgroundColor: '#26a69a',
              color: '#fff',
              border: 'none',
              borderRadius: '4px',
              cursor: 'pointer',
              flex: 1,
              marginRight: editingId ? '0.5rem' : 0
            }}>
              {editingId ? 'Salvar' : 'Enviar'}
            </button>
            {editingId && (
              <button type="button" onClick={handleCancel} style={{
                padding: '0.5rem 1rem',
                backgroundColor: '#757575',
                color: '#fff',
                border: 'none',
                borderRadius: '4px',
                cursor: 'pointer',
                flex: 1
              }}>
                Cancelar
              </button>
            )}
          </div>
        </form>

        <h3 style={{ marginTop: '2rem' }}>Imagens Salvas</h3>
        <div style={{
          display: 'grid',
          gridTemplateColumns: 'repeat(auto-fit, minmax(140px, 1fr))',
          gap: '1rem',
          marginTop: '1rem'
        }}>
          {visibleImages.map((img) => (
            <div key={img.id} style={{
              backgroundColor: '#2a2a2a',
              padding: '0.5rem',
              borderRadius: '6px'
            }}>
              <img
                src={`http://localhost:3010/uploads/${img.filename}`}
                alt={img.name}
                style={{
                  width: '100%',
                  borderRadius: '6px',
                  cursor: 'pointer',
                  marginBottom: '0.5rem'
                }}
                onClick={() => setLightboxImage(`http://localhost:3010/uploads/${img.filename}`)}
              />
              <p style={{ fontSize: '0.9rem', marginBottom: '0.5rem' }}>
                <strong>{img.name}</strong><br />
                {img.description}
              </p>
              <div style={{ display: 'flex', gap: '0.5rem' }}>
              <button onClick={() => confirmDelete(img)} style={{
                  flex: 1,
                  backgroundColor: '#2ecc71',
                  color: '#fff',
                  border: 'none',
                  padding: '0.4rem',
                  borderRadius: '4px',
                  cursor: 'pointer',
                  fontSize: '0.8rem'
                }}>Editar</button>
                <button onClick={() => handleDelete(img.id)} style={{
                  flex: 1,
                  backgroundColor: '#e74c3c',
                  color: '#fff',
                  border: 'none',
                  padding: '0.4rem',
                  borderRadius: '4px',
                  cursor: 'pointer',
                  fontSize: '0.8rem'
                }}>Remover</button>
              </div>
            </div>
          ))}
        </div>
      </div>

      {lightboxImage && (
        <div style={{
          position: 'fixed',
          top: 0, left: 0, right: 0, bottom: 0,
          backgroundColor: 'rgba(0,0,0,0.85)',
          display: 'flex',
          alignItems: 'center',
          justifyContent: 'center',
          zIndex: 9999
        }}>
          <div style={{ position: 'relative' }}>
            <img
              src={lightboxImage}
              alt="Visualização ampliada"
              style={{
                maxWidth: '90vw',
                maxHeight: '90vh',
                borderRadius: '8px'
              }}
            />
            <button
              onClick={() => setLightboxImage(null)}
              style={{
                position: 'absolute',
                top: '-10px',
                right: '-10px',
                background: '#e74c3c',
                border: 'none',
                borderRadius: '50%',
                color: '#fff',
                width: '32px',
                height: '32px',
                fontWeight: 'bold',
                cursor: 'pointer'
              }}
            >
              ×
            </button>
          </div>
        </div>
      )}
    </div>
  );
}

export default App;
