import React, { useState, useEffect } from "react";
import { FiEdit2, FiTrash2 } from "react-icons/fi";
import Login from "./login";
import Register from "./Register";

// 🎯 Estilos reutilizáveis
const buttonStyle = {
  padding: "0.5rem 1rem",
  border: "none",
  borderRadius: "4px",
  fontSize: "0.9rem",
  cursor: "pointer",
};

const inputStyle = {
  width: "480px",
  padding: "0.5rem",
  marginBottom: "1rem",
  borderRadius: "4px",
  backgroundColor: "#2a2a2a",
  color: "#fff",
  border: "1px solid #444",
  display: "block",
  marginLeft: "auto",
  marginRight: "auto",
};

function App() {
  const [form, setForm] = useState({
    name: "",
    description: "",
    image: null,
  });
  const [preview, setPreview] = useState(null);
  const [lightboxImage, setLightboxImage] = useState(null);
  const [images, setImages] = useState([]);
  const [editingId, setEditingId] = useState(null);
  const [pendingDelete, setPendingDelete] = useState(null);
  const [searchInput, setSearchInput] = useState("");
  const [searchTerm, setSearchTerm] = useState("");
  const [page, setPage] = useState(1);
  const [loading, setLoading] = useState(false);
  const [hasMore, setHasMore] = useState(true);
  const [total, setTotal] = useState(0);
  const limit = 10;
  const [sortOrder, setSortOrder] = useState("recent");
  const [user, setUser] = useState(null);
  const [showRegister, setShowRegister] = useState(false);
  const [token, setToken] = useState(localStorage.getItem("token"));
  const [avisoImagem, setAvisoImagem] = useState(false);
  const [loadingGlobal, setLoadingGlobal] = useState(false);
  const [mensagemLoading, setMensagemLoading] = useState("");

  // ⏱️ Utilitário
  const delay = (ms) => new Promise((resolve) => setTimeout(resolve, ms));

  useEffect(() => {
    fetchImages(page, searchTerm, sortOrder);
  }, [page, searchTerm, sortOrder]);

  // 📸 Preview de imagem clicável (tela interna)
  const renderPreview = () =>
    preview && (
      <div style={{ textAlign: "center", marginBottom: "1rem" }}>
        <img
          src={preview}
          alt="Prévia gerada"
          style={{
            maxWidth: "200px",
            maxHeight: "200px",
            width: "100%",
            height: "auto",
            borderRadius: "6px",
            boxShadow: "0 0 8px rgba(0,0,0,0.5)",
            objectFit: "contain",
            cursor: "pointer",
          }}
          onClick={() => setLightboxImage(preview)}
        />
      </div>
    );

  // 🧠 Lógica de submit e demais funções
  // Mantidas intactas — se quiser, posso colar aqui também separadamente

  // ✨ Conteúdo renderizado
  if (!user) {
    return showRegister ? (
      <Register onRegisterSuccess={() => setShowRegister(false)} />
    ) : (
      <Login
        onLogin={(username) => {
          const storedToken = localStorage.getItem("token");
          setUser(username);
          setToken(storedToken);
        }}
        onShowRegister={() => setShowRegister(true)}
      />
    );
  }

  return (
    <div
      style={{
        backgroundColor: "#121212",
        color: "#f0f0f0",
        minHeight: "100vh",
        fontFamily: "Arial, sans-serif",
        padding: "2rem",
      }}
    >
      {/* 🔴 Botão de logout */}
      <div style={{ textAlign: "right", marginBottom: "1rem" }}>
        <button
          onClick={() => setUser(null)}
          style={{ ...buttonStyle, backgroundColor: "#e74c3c", color: "#fff" }}
        >
          Sair
        </button>
      </div>

      {/* 📋 Formulário principal */}
      <div
        style={{
          backgroundColor: "#1e1e1e",
          padding: "2rem",
          borderRadius: "8px",
          boxShadow: "0 0 10px rgba(0,0,0,0.4)",
        }}
      >
        <h2 style={{ textAlign: "center", marginBottom: "1.5rem" }}>
          {editingId ? "Editar Imagem" : "Upload de Imagem"}
        </h2>

        {/* ✨ Formulário: name, description, image, botões */}
        {/* ⚠️ Aqui segue sua estrutura original, com melhorias se você quiser */}
        {/* ... */}
        {renderPreview()}
        {/* ... restante da renderização de formulário, lista de imagens, paginação, modais, etc. */}
      </div>
    </div>
  );
}

export default App;
